package org.b3log.leetcode.easy;

import java.util.HashMap;
import java.util.Map;

/**
 * @author : yu.zhang
 * Date : 2018/6/27 下午5:40
 * Email : yu.zhang@7fresh.com
 **/
public class P389_FindTheDifference {
    public static char findTheDifference(String s, String t) {
        Map<Character, Integer> sMap = new HashMap<>();
        Map<Character, Integer> tMap = new HashMap<>();
        for (char c : s.toCharArray()) {
            sMap.merge(c, 1, (a, b) -> a + b);
        }
        for (char c : t.toCharArray()) {
            tMap.merge(c, 1, (a, b) -> a + b);
        }
        for (Character c : tMap.keySet()) {
            if (sMap.get(c) == null) {
                return c;
            } else if (!sMap.get(c).equals(tMap.get(c))) {
                return c;
            }
        }
        return 0;
    }

    public static char findTheDifference2(String s, String t) {
        return (char) (t.chars().sum() - s.chars().sum());
    }

    public static void main(String[] args) {
        System.out.println(findTheDifference("abcd", "abcde"));
        System.out.println(findTheDifference("",
                ""));
    }
}
